    private void EqualsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EqualsActionPerformed
        switch(pilih){
        case 1:
            bil2 = Double.parseDouble(String.valueOf(TextField.getText()));
            jumlah = bil1+bil2;
            bilangan = Double.toString(jumlah);            
            break;
        case 2:
            bil2 =Double.parseDouble(String.valueOf(TextField.getText()));
            jumlah = bil1 - bil2;
            bilangan = Double.toString(jumlah);            
            break;
        case 3:
            bil2 = Double.parseDouble(String.valueOf(TextField.getText()));
            jumlah = bil1 * bil2;
            bilangan = Double.toString(jumlah);            
            break;
        case 4:
            bil2 = Double.parseDouble(String.valueOf(TextField.getText()));
            jumlah = bil1 / bil2;
            bilangan = Double.toString(jumlah);            
            break;            
        case 5:
            bil2 = Double.parseDouble(String.valueOf(TextField.getText()));
            jumlah = bil1 % bil2;
            bilangan = Double.toString(jumlah);            
            break;            
        }
        TextField.setText(bilangan);    }//GEN-LAST:event_EqualsActionPerformed

