    private void SquareActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SquareActionPerformed
        bil1 = Double.parseDouble(bilangan);
        if(bil1 > 0) {
        }
        bil1 = bil1 * bil1;
        bilangan = Double.toString(bil1);
        TextField.setText(Double.toString(bil1));
    }//GEN-LAST:event_SquareActionPerformed

    private void SquareRootActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SquareRootActionPerformed
        bil1 = Double.parseDouble(bilangan);
        if(bil1 >= 0) {
            bil1 = Math.sqrt(bil1);
            bilangan = Double.toString(bil1);
            TextField.setText(Double.toString(bil1));
        } else {
            TextField.setText("Cannot sqrt negative number");
        }

    }//GEN-LAST:event_SquareRootActionPerformed

